for role in `echo aws_iam_role_auditors aws_iam_role_be_developers aws_iam_role_data_scienztists aws_iam_role_developers aws_iam_role_devops aws_iam_role_fe_developers aws_iam_role_infra_admins`; do while read -r l; do eval "$l"; env=$(echo $acc|cut -f3 -d-); for prefix in '' 'be_' 'fe_'; do echo "[$(echo $acc | sed s/rcr/${prefix}dev/g)](https://signin.aws.amazon.com/switchrole?account=$id&roleName=$ORG/$APP/${env}/$role&displayName=$(echo $acc| sed s/rcr/${prefix}dev/g))"; done ;done < <(aws organizations list-accounts --profile rtr_adm| jq -r '.Accounts[]|select(.Name | endswith("rcr")) | select(.Status | startswith("ACTIVE")) | "id="+.Id + "; acc="+.Name' | grep -v nba)|sort -k3 -t=; done ;
